const ITEMS_PER_PAGE = 10;
export default function ApplicantPage() {
    //--------------------------------Action menu--------------------------
      const [menuOpen, setMenuOpen] = useState(false);
      const menuRef = useRef<HTMLDivElement>(null);
      const [menuPos, setMenuPos] = useState<{ top: number; left: number } | null>(null);
      // ---------------------------------------------------------------------
  //---------------------filter----------------------------------
      // Multi-select arrays instead of single string
      type ContractStatus = "Running" | "Completed" | "All";
  
      const CONTRACT_STATUSES: ContractStatus[] = ["Running", "Completed", "All"];
      const [editingContract, setEditingContract] = useState<any>(null);
      const [selectedContractStatus, setSelectedContractStatus] = useState<ContractStatus[]>([]);
      const [showApplicants, setShowApplicants] = useState(true);
      const router = useRouter();
      const [searchQuery, setSearchQuery] = useState("");
      const [currentPage, setCurrentPage] = useState(1);
      const [isModalOpen, setIsModalOpen] = useState(false);
  
      //  Filter applicants
      const filteredApplicants = useMemo(() => {
        return mockApplicants.filter(applicant => {
          // Search filter
          const searchMatch =
            !searchQuery.trim() ||
            applicant.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
            applicant.applicantId.toLowerCase().includes(searchQuery.toLowerCase()) ||
            applicant.contractTitle.toLowerCase().includes(searchQuery.toLowerCase()) ||
            applicant.startDate.toLowerCase().includes(searchQuery.toLowerCase()) ||
            applicant.endDate.toLowerCase().includes(searchQuery.toLowerCase());
  
          // Contract status filter
          const contractMatch =
            selectedContractStatus.length === 0 || // empty = no filter
            selectedContractStatus.includes("All") || // All = include all
            selectedContractStatus.includes(applicant.contractStatus as "Running" | "Completed");
  
      
  
          return searchMatch && contractMatch;
        });
      }, [searchQuery, selectedContractStatus]);
  
  
      
      //  Pagination
      const totalPages = Math.ceil(filteredApplicants.length / ITEMS_PER_PAGE);
      
      const paginatedApplicants = filteredApplicants.slice(
        (currentPage - 1) * ITEMS_PER_PAGE,
        currentPage * ITEMS_PER_PAGE
      );
      
      const goToPage = (page: number) => {
        if (page >= 1 && page <= totalPages) setCurrentPage(page);
      };
      
      const goToPrevious = () => {
        if (currentPage > 1) setCurrentPage(prev => prev - 1);
      };
      
      const goToNext = () => {
        if (currentPage < totalPages) setCurrentPage(prev => prev + 1);
      };
  

  return (
    <DashboardLayout>
     <div className="px-4 sm:px-6 lg:px-8 py-6">
        <h1 className="text-[28px] font-bold text-[#1E1E1E] mb-8">
          Top 10 Student Profile
        </h1>
     
        {/* Main column */}
        <div className="flex flex-col gap-6">
          
            
           {/* Applicants Grid */}
          {showApplicants && (
          <div className="grid grid-cols-5 grid-rows-2 gap-2">
          {paginatedApplicants.map((applicant, index) => (
            <ApplicantCard
              key={index}
              applicant={applicant}
              onClick={() => router.push(`/Company/applicant/${applicant.applicantId}`)}
            />
          ))}
        </div>
        )}

         {/* Contracts Grid */}
      {!showApplicants && (
        <div className="flex flex-col gap-2 mt-4">
          {paginatedApplicants.map((applicant, index) => (
            <ContractRow
              key={index}
              applicant={applicant}
              onMainClick={() => router.push(`/Company/applicant/${applicant.applicantId}`)}
              showCheckbox={true} // optional
            />
          ))}
        </div>
      )}

        </div>
      </div>
       {totalPages > 1 && (
        <div className="flex items-center justify-center gap-3 mt-10">
                      {/* Previous */}
                      <button
                        onClick={goToPrevious}
                        disabled={currentPage === 1}
                        className="px-3 py-1 text-sm font-medium rounded-md 
                                  disabled:opacity-40 disabled:cursor-not-allowed"
                      >
                      <img 
                      src="/ic-eva_arrow-ios-back-fill.svg" 
                      alt="Applied Students"
                    />
                      </button>

                    {/* Page numbers */}
                      {Array.from({ length: totalPages }, (_, i) => i + 1).map(page => (
                        <button
                          key={page}
                          onClick={() => goToPage(page)}
                          className={`
                            relative
                            w-11 h-9
                            skew-x-[-12deg]
                            rounded-md
                            overflow-hidden
                            text-sm font-semibold
                            transition-all duration-200
                            ${
                              currentPage === page
                                ? "border border-black text-black"
                                : "border border-transparent text-black/60 hover:bg-black/10 hover:text-black"
                            }
                          `}
                        >
                          {/* Un-skew content */}
                          <span className="flex h-full w-full items-center justify-center skew-x-[12deg]">
                            {page}
                          </span>
                        </button>
                      ))}



                      {/* Next */}
                      <button
                        onClick={goToNext}
                        disabled={currentPage === totalPages}
                        className="px-3 py-1 text-sm font-medium rounded-md 
                                  disabled:opacity-40 disabled:cursor-not-allowed"
                      >
                        <img 
                      src="/ic-eva_arrow-ios-forward-fill.svg" 
                      alt="Applied Students"
                    />
                      </button>
        </div>
        )}

    </DashboardLayout>
  );
}